        .error__overlay {
            position: absolute;
            top: 0;
            left: 0;
            width: 100%;
            height: 100%;
            overflow: hidden;
        }

        .error__content {
            position: absolute;
            top: 50%;
            left: 50%;
            width: 100%;
            -webkit-transform: translate(-50%, -50%);
            transform: translate(-50%, -50%);
        }

        .error__message {
            text-align: center;
            color: #181828;
        }

        .message__title {
            font-family: 'Montserrat', sans-serif;
            font-weight: 900;
            text-transform: uppercase;
            letter-spacing: 5px;
            font-size: 5rem;
            padding-bottom: 40px;
            max-width: 960px;
            margin: 0 auto;
        }

        .message__text {
            font-family: 'Montserrat', sans-serif;
            line-height: 42px;
            font-size: 18px;
            padding: 0 60px;
            max-width: 680px;
            margin: auto;
        }

        .error__nav {
            max-width: 600px;
            margin: 40px auto 0;
            text-align: center;
        }

        .e-nav__form {
            position: relative;
            height: 45px;
            overflow: hidden;
            width: 170px;
            display: inline-block;
            vertical-align: top;
            border: 1px solid #212121;
            padding-left: 10px;
            padding-right: 46px;
        }

        .e-nav__icon {
            position: absolute;
            right: 15px;
            top: 50%;
            -webkit-transform: translateY(-50%);
            transform: translateY(-50%);
            color: #212121;
            -webkit-transition: color .25s ease;
            transition: color .25s ease;
        }

        .e-nav__link {
            height: 45px;
            line-height: 45px;
            width: 170px;
            display: inline-block;
            vertical-align: top;
            margin: 0 15px;
            border: 1px solid #181828;
            color: #181828;
            text-decoration: none;
            font-family: 'Montserrat', sans-serif;
            text-transform: uppercase;
            font-size: 11px;
            letter-spacing: .1rem;
            position: relative;
            overflow: hidden;
        }

        .e-nav__link:before {
            content: '';
            height: 200px;
            background: #212121;
            position: absolute;
            top: 70px;
            right: 70px;
            width: 260px;
            -webkit-transition: all .3s;
            transition: all .3s;
            -webkit-transform: rotate(50deg);
            transform: rotate(50deg);
        }

        .e-nav__link:after {
            -webkit-transition: all .3s;
            transition: all .3s;
            z-index: 999;
            position: relative;
        }

        .e-nav__link:after {
            content: "Home Page";
        }

        .e-nav__link:hover:before {
            top: -60px;
            right: -50px;
        }

        .e-nav__link:hover {
            color: #fff;
        }

        .e-nav__link:nth-child(2):hover:after {
            color: #fff;
        }

        .error__social {
            position: absolute;
            top: 50%;
            -webkit-transform: translateY(-50%);
            transform: translateY(-50%);
            left: 20px;
            z-index: 10;
        }

        .e-social__list {
            margin: 0;
            padding: 0;
            list-style-type: none;
        }

        .e-social__icon {
            padding-bottom: 30px;
        }

        .e-social__icon:last-child {
            padding-bottom: 0;
        }

        .e-social__link {
            color: #fff;
            -webkit-transition: all .25s ease;
            transition: all .25s ease;
            display: block;
        }

        .e-social__link:hover {
            opacity: .7;
        }

        .lamp {
            position: absolute;
            left: 0px;
            right: 0px;
            top: 0px;
            margin: 0px auto;
            width: 300px;
            display: flex;
            flex-direction: column;
            align-items: center;
            transform-origin: center top;
            animation-timing-function: cubic-bezier(0.6, 0, 0.38, 1);
            animation: move 5.1s infinite;
        }

        @keyframes move {
            0% {
                transform: rotate(40deg);
            }

            50% {
                transform: rotate(-40deg);
            }

            100% {
                transform: rotate(40deg);
            }
        }

        .cable {
            width: 8px;
            height: 248px;
            background-image: linear-gradient(rgb(32 148 218 / 70%), rgb(193 65 25)), linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7)), linear-gradient(rgba(0, 0, 0, 0.7), rgba(0, 0, 0, 0.7));
